import React,{Component} from 'react'

import classes from './Button.css'

class button extends Component {

    handleClick = () => {
        this.audio.play();
        this.audio.currentTime = 0
    };
    render() {
        return (<div className="drum-pad"
                     id={this.props.id}
                     handleClick
            >
                <audio
                    className="clip"
                    id={this.props.value}
                    src={this.props.src}
                    ref={ref => this.audio = ref}/>
                {this.props.value}
            </div>
        );
    }

};
export default button;
